const apiKey="9684a6d8834c6f067df1da442108e8f9",weatherBtn=document.getElementById("btnSearch");async function getWeather(){const e=document.getElementById("search").value.trim(),t=document.querySelector("#container");if(!e)return void alert("Please enter a city name");const n=`https://api.openweathermap.org/data/2.5/weather?q=${e}&units=metric&appid=${apiKey}`;try{const a=await fetch(n),r=await a.json();if(200===r.cod){t.className="weather-details",t.style.display="flex";const n=r.main.temp,a=r.main.feels_like,i=r.main.pressure,c=r.main.humidity,s=r.weather[0].description,o=`https://openweathermap.org/img/wn/${r.weather[0].icon}@2x.png`;t.innerHTML=`\n      \n        <h2>${e}</h2>\n        <img src="${o}" alt="weather icon" />\n        <h3>${s}</h3>\n        <p>Temperature: ${n}°C</p>\n        <p>Feels Like: ${a}°C</p>\n        <p>Pressure: ${i} hPa</p>\n        <p>Humidity: ${c}%</p>\n      `}else alert("City not found"),t.innerHTML=""}catch(e){t.innerText="⚠️ Failed to fetch weather data.",console.error(e)}}weatherBtn.addEventListener("click",getWeather);